/* Minimal CSS */
* { margin: 0; padding: 0; box-sizing: border-box; }
body { font-family: 'Arial', sans-serif; background-color: #f4f4f4; color: #333; }
header { background-color: #20232a; color: #61dafb; padding: 1rem; text-align: center; }
main { padding: 2rem; display: flex; justify-content: center; align-items: center; }
footer { background-color: #20232a; color: white; text-align: center; padding: 1rem; }
